$select left(V_INVENTORY_MSTR.PART,17) as "Part Number", right(V_INVENTORY_MSTR.PART,3) as "Rev", V_INVENTORY_MSTR.LOCATION as "Loc", V_INVENTORY_MSTR.DESCRIPTION as "Part Description", V_INV_CROSS_REF.CUSTOMER_PART as "Customer Part", V_INVENTORY_MSTR.QTY_ONHAND as "On Hand", V_INVENTORY_MSTR.QTY_ONORDER_PO "On Order PO", V_INVENTORY_MSTR.QTY_ONORDER_WO as "On Order WO", V_INVENTORY_MSTR.QTY_REQUIRED as "Requirements", cast(V_INVENTORY_MSTR.QTY_ONHAND+V_INVENTORY_MSTR.QTY_ONORDER_PO+V_INVENTORY_MSTR.QTY_ONORDER_WO-V_INVENTORY_MSTR.QTY_REQUIRED as decimal(30,4)) as "Net" @'M'*!*V_INVENTORY_MSTR.PART*!*NULL*!*V_INVENTORY_MSTR.LOCATION*!*NULL@ from V_INVENTORY_MSTR inner join V_INV_CROSS_REF on V_INVENTORY_MSTR.PART = V_INV_CROSS_REF.PART and V_INVENTORY_MSTR.LOCATION = V_INV_CROSS_REF.LOCATION order by "Part Number", "Rev", "Loc"